import { Component, OnInit ,NgZone , OnDestroy} from '@angular/core';
import { Observable } from 'rxjs';
import * as am4core from "@amcharts/amcharts4/core";
import * as am4charts from "@amcharts/amcharts4/charts";
import am4themes_animated from "@amcharts/amcharts4/themes/animated";
am4core.useTheme(am4themes_animated);
export interface ChartElement {
  date: string;
  shareRate: number;
  bullet:string;
}

const CHART_DATA: ChartElement[] = [{
    "date": "1-Jan-2018",
    "shareRate": 501,
    "bullet": "",
  },
  {
    "date": "3-Jan-2018",
    "shareRate": 301,
    "bullet": "",
  },
  {
    "date": "4-Jan-2018",
    "shareRate": 201,
    "bullet": "",
  },
  {
    "date": "6-Jan-2018",
    "shareRate": 500,
    "bullet": "",
  },
  {
    "date": "8-Jan-2018",
    "shareRate": 300,
    "bullet": "",
  },
  {
    "date": "15-Jan-2018",
    "shareRate": 659,
    "bullet": "",
  },
  {
    "date": "18-Jan-2018",
    "shareRate": 700,
    "bullet": "",
  },
  {
    "date": "25-Jan-2018",
    "shareRate": 200,
    "bullet": "",
  }]
@Component({
  selector: 'app-chart',
  templateUrl: './chart.component.html',
  styleUrls: ['./chart.component.css']
})
export class ChartComponent implements OnDestroy {
private chart: am4charts.XYChart;
  constructor(private zone: NgZone) {}

  showchart(){
  	this.zone.runOutsideAngular(() => {
      let chart = am4core.create("chartdiv", am4charts.XYChart);
      chart.paddingRight = 20;
      let datedata=this.getchartdata(CHART_DATA,CHART_DATA.length);
      let data = [];
      for (let i = 0; i < datedata.length; i++) {
        data.push({ date: new Date(datedata[i].date), value: datedata[i].shareRate , bullet:datedata[i].bullet});
      }
      chart.data = data;
      let dateAxis = chart.xAxes.push(new am4charts.DateAxis());
      dateAxis.renderer.grid.template.location = 0;

      let valueAxis = chart.yAxes.push(new am4charts.ValueAxis());
      valueAxis.tooltip.disabled = true;
      valueAxis.renderer.minWidth = 35;

      let series = chart.series.push(new am4charts.LineSeries());
      series.dataFields.dateX = "date";
      series.dataFields.valueY = "value";
      series.name = "Sales";
      series.strokeWidth = 2;

let bullet = series.bullets.push(new am4charts.Bullet());
let image = bullet.createChild(am4core.Image);
image.width = 42;
image.height = 42;
image.horizontalCenter = "middle";
image.verticalCenter = "middle";
image.propertyFields.href = "bullet";
image.filters.push(new am4core.DropShadowFilter());
      this.chart = chart;
    });
  }

getchartdata(share: any, n: number) {
        let max_diff = share[1].shareRate - share[0].shareRate;
        var days = []; 
        for (let i = 0; i < n; i++) {
          for (let j = i + 1; j < n; j++) {
            if (share[j].shareRate - share[i].shareRate > max_diff) {
              max_diff = share[j].shareRate - share[i].shareRate;
              days = [share[i].date, share[j].date, max_diff];
            }
          }
        }
        for(let k=0;k<share.length;k++){
        	if(share[k].date==days[0]){
        		share[k].bullet="data:image/png;base64,";
        	}
        	if(share[k].date==days[1]){
        		share[k].bullet="data:image/png;base64,";
        	}
        }
        return share;
      }


  ngOnDestroy() {
    this.zone.runOutsideAngular(() => {
      if (this.chart) {
        this.chart.dispose();
      }
    });
  }

}
